import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Params} from "@angular/router";
import {Observable} from "rxjs";
import {switchMap} from "rxjs/operators";

import {PostInterface} from "../admin/shared/interfaces/post.interface";
import {PostService} from "../admin/shared/services/post.service";

@Component({
  selector: 'app-post-page',
  templateUrl: './post-page.component.html',
  styleUrls: ['./post-page.component.scss']
})
export class PostPageComponent implements OnInit {

  post$: Observable<PostInterface> | undefined

  constructor(private route: ActivatedRoute, private postService: PostService) {
  }

  ngOnInit(): void {
    this.post$ = this.route.params
      .pipe(
        switchMap((params: Params): Observable<PostInterface> => {
            return this.postService.getPostId(params['id']);
          }
        )
      )
  }

}
<div *ngIf="post$ | async as post; else loading">
  <mat-card class="mat__card">
    <div class="post">
      <div class="header">
        <h1>{{post.title}}</h1>

        <button routerLink="/" class="post__page_button" mat-raised-button>Post page</button>
      </div>

      <div class="info">
        <strong>{{post.author}}</strong>
        <small>{{post.date | date:'medium':'ru': 'ru'}}</small>
      </div>
      <div>
        <quill-view-html [content]="post.text"></quill-view-html>
      </div>
    </div>
  </mat-card>
</div>
<ng-template #loading>
  <p class="text-centre">Post loading...</p>
</ng-template>
