import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {UserService} from "../shared/services/user.service";
import {tap} from "rxjs/operators";
import {Router} from "@angular/router";
import {AuthService} from "../shared/services/auth.service";
import {MatDialog} from "@angular/material/dialog";
import {ToastrService} from "ngx-toastr";

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.scss']
})
export class ChangePasswordComponent implements OnInit {
  form: FormGroup | any;
  changePassword$: Observable<any> | undefined;

  constructor(
    private userService: UserService,
    private router: Router,
    private authService: AuthService,
    private dialog: MatDialog,
    private toastrService: ToastrService
  ) {
  }

  ngOnInit(): void {
    this.formInitialize();
  }

  formInitialize() {
    this.form = new FormGroup({
        newPassword: new FormControl('', Validators.required)
      }
    )
  }

  modalClose() {
    this.dialog.closeAll();
  }

  submit() {
    if (this.form.invalid) {
      return
    }

    const newPassword = this.form.value.newPassword;

    this.changePassword$ = this.userService.changePassword(newPassword).pipe(
      tap(() => {
          this.form.reset();
          this.modalClose();
          this.toastrService.success('Password changed');
          this.toastrService.error('Login again');
          this.router.navigate(['/admin/login']);
          this.authService.logout();
        }
      )
    );

  }
}
<h1 class="change__password_title" mat-dialog-title>Change password modal</h1>
<form action="" [formGroup]="form" (ngSubmit)="submit()">
  <div class="input__change">
    <label for="inputPassword" class="label__password">New password</label>
    <input id="inputPassword" class="input__password" type="text" formControlName="newPassword">
  </div>
  <div *ngIf="form.get('newPassword').touched && form.get('newPassword').errors?.required">
    <small style="color: red">Full name not be empty</small>
  </div>
  <div class="btns" mat-dialog-actions>
    <button mat-raised-button mat-button mat-dialog-close>Cancel</button>
    <button [ngClass]="{'bat__invalid': form.invalid}"
            class="btn__change_password"
            mat-raised-button color="primary"
            mat-button type="submit"
            [disabled]="form.invalid">Ok
    </button>
  </div>
</form>

<ng-container *ngIf="changePassword$ | async"></ng-container>
