import {Component, ElementRef, OnDestroy, OnInit, ViewChild} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";
import {Subscription} from "rxjs";

import {UserInterface} from "../shared/interfaces/user.interface";
import {AuthService} from "../shared/services/auth.service";
import {MatCheckboxChange} from "@angular/material/checkbox";

@Component({
  selector: 'app-sing-up',
  templateUrl: './sing-up.component.html',
  styleUrls: ['./sing-up.component.scss']
})
export class SingUpComponent implements OnInit, OnDestroy {

  @ViewChild('passwordInput') passwordInput: ElementRef | undefined;
  form: FormGroup | any;
  singUpSub$: Subscription | undefined;
  isDisabled: boolean = true;


  constructor(private authService: AuthService, private route: Router) {
  }

  ngOnInit(): void {
    this.initializeForm();

  }

  initializeForm(): void {
    this.form = new FormGroup({
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [Validators.required, Validators.minLength(6)])
    })
  }

  submit() {
    if (this.form.valid) {
      const user: UserInterface = {
        email: this.form.value.email,
        password: this.form.value.password
      }

      this.singUpSub$ = this.authService.singUp(user)
        .subscribe(
          () => {
            this.route.navigate(['/admin', 'dashboard']);
            this.form.reset();
          }
        );

    }
  }

  changeShowPassword(event: MatCheckboxChange) {
    if (event.checked) {
      this.passwordInput?.nativeElement.setAttribute('type','text');
    } else {
      this.passwordInput?.nativeElement.setAttribute('type','password');
    }
  }

  ngOnDestroy(): void {
    this.singUpSub$?.unsubscribe();
  }
}
<mat-card class="card__wrapper">
  <form class="card" [formGroup]="form" (ngSubmit)="submit()">
    <h2>Sing-up form</h2>

    <div
      [ngClass]="{invalid: form.get('email').touched && form.get('email').invalid}"
      class="form-control">
      <label for="email">Email</label>
      <input id="email" type="email" formControlName="email">
      <div *ngIf="form.get('email').touched && form.get('email').invalid">
        <small class="form-validation" *ngIf="form.get('email').errors.required">Email must not be empty</small>
        <small class="form-validation" *ngIf="form.get('email').invalid">Enter correct email</small>
      </div>
    </div>

    <div
      [ngClass]="{invalid: form.get('password').touched && form.get('password').invalid}"
      class="form-control">
      <label for="password">Password</label>
      <input id="password" type="password" formControlName="password" #passwordInput>
      <div *ngIf="form.get('password').touched && form.get('password').invalid">
        <small class="form-validation" *ngIf="form.get('password').errors.required">
          Password must not be empty
        </small>
        <small class="form-validation" *ngIf="form.get('password').errors.minlength && form.get('password').invalid">
          Length password not be less
          {{form.get('password').errors.minlength.requiredLength}}
          symbol. Now length
          {{form.get('password').errors.minlength.actualLength}}.
        </small>
      </div>
    </div>
    <mat-checkbox
      class="checkbox__password"
      (change)="changeShowPassword($event)"
    >Show password
    </mat-checkbox>

    <div class="button__wrapper">
    <button class="btn btn-primary" type="submit" [disabled]="form.invalid">Sing-up</button>
    </div>
  </form>
</mat-card>
